INSERT INTO `tb_produk` (`kategori_id`, `produk_id`, `produk_nama`, `produk_tagline`, `produk_isi`, `produk_keunggulan`, `produk_foto`) VALUES
(1, 1, 'SmartLink New Flexi Account Plus', 'Program Asuransi Jiwa untuk Meraih Masa Depan Anda dan Keluarga', '<p>Program Asuransi Jiwa Unit Link dari Allianz bagi Anda.</p>\r\n\r\n<p>Inilah solusi pengembangan aset finansial agar Anda terhindar dari risiko yang mungkin terjadi di masa depan.</p>\r\n\r\n<p>Sekarang saat yang tepat bagi kita untuk memilih asuransi jiwa terbaik agar mimpi Anda jadi nyata.</p>\r\n', '<p>Program asuransi jiwa unit link yang memberikan perlindungan nilai investasi menyeluruh bagi keluarga baik saat Anda masih ada ataupun sudah tiada.</p>\r\n', '1600766041_smartlink new flexi.png'),
(2, 2, 'Smartmed Premier', 'SmartMed Premier merupakan asuransi kesehatan yang memberikan perlindungan menyeluruh bagi Anda dan keluarga', '<p>SmartMed Premier adalah asuransi kesehatan yang dapat digunakan secara perorangan dan keluarga.</p>\r\n', '<ul>\r\n	<li>Fasilitas medis kelas VIP dengan kemudahan Cashless di rumah sakit jaringan Allianz di Indonesia, Singapura, Malaysia, Thailand, dan lain-lain.</li>\r\n	<li>Total Manfaat dalam setahun yang diterima pemegang polis hingga Rp 6 miliar per orang.</li>\r\n	<li>Penggantian biaya perawatan sesuai tagihan (as charge) jika perawatan sesuai Plan.</li>\r\n	<li>Bila memilih kamar lebih dari Plan atau memilih rumah sakit di Amerika Serikat, maka biaya perawatan akan dibayaroleh pemegang polis sesuai Limit Dasar.</li>\r\n	<li>Menyediakan Manfaat Rawat Inap dan Manfaat Tambahan seperti Persalinan, Rawat Jalan, serta Rawat Gigi.</li>\r\n	<li>Menyediakan Manfaat Spesial yaitu Kemoterapi, Hemodialisis, Evakuasi Medis dan Pemulangan Darurat, HIV/AIDS, serta Biaya Pemakaman.</li>\r\n	<li>No Claim Bonus atau diskon bonus sebesar 20% yang diberikan saat perpanjangan polis bila tidak ada catatan klaim selama satu tahun polis.</li>\r\n	<li>Diskon bonus dihitung dari premi polis tahun lalu dan berlaku untuk premi per Tertanggung.</li>\r\n	<li>Fasilitas Allianz eAZy Claim dan Reimbursement untuk penggantian biaya perawatan medis yang dilakukan di rumah sakit di luar Jaringan Allianz-Admedika.</li>\r\n	<li>Pilihan layanan co-share 20%, 10%, dan 0% sesuai perjanjian antara Tertanggung dengan Allianz.</li>\r\n	<li>Akses Allianz Medical Hotline 24 jam untuk kebutuhan informasi dan layanan medis, serta Jasa Layanan Bantuan Medis lnternasional.</li>\r\n</ul>\r\n', '1600766113_smartmed premier.png'),
(1, 3, 'SmartLink Maxi Fund Plus', 'Program asuransi jiwa yang dirancang khusus bagi Anda yang memimpikan perlindungan seumur hidup plus investasi optimal bagi Anda dan keluarga.', '<p>Program asuransi ini menawarkan perlindungan jiwa seumur hidup hingga usia 100 tahun</p>\r\n\r\n<p>Program asuransi jiwa ini juga menawarkan manfaat Investasi lewat produk unit link. Anda dapat memilih alokasi dari nilai investasi Anda.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Tingkat keamanan dan stabilitas tinggi dengan risiko rendah Tingkat keamanan, stabilitas tinggi dengan risiko rendah hingga sedang Tingkat keamanan dan stabilitas tinggi dengan risiko sedang Tingkat keamanan dan stabilitas tinggi dengan risiko tinggi Diinvestasikan seperti deposito, SBI, juga obligasi dan saham Tingkat keamanan dan stabilitas tinggi serta risiko rendah hingga sedang</p>\r\n', '1600766309_SmartLink Maxi fund plus.png'),
(1, 4, 'SmartLink Flexi Account Plus', 'Melindungi langkah hidup untuk meraih masa depan Anda & keluarga', '<p>Selama langkah Anda untuk mewujudkan prioritas mimpi dalam hidup, Anda harus menemukan solusi pengembangan aset finansial yang maksimal.</p>\r\n\r\n<p>Namun apakah kita memiliki jaminan akan terhindar dari risiko kehidupan yang mungkin terjadi di masa depan? Sudah saatnya kita mulai mempersiapkan perlindungan terbaik untuk prioritas mimpi dalam hidup kita.</p>\r\n', '<p>Program asuransi jiwa unit link yang memberikan perlindungan maksimal disertai potensi nilai investasi yang membantu mewujudkan prioritas mimpi di saat berada di samping keluarga tercinta maupun ketika sudah meninggalkan mereka.</p>\r\n', '1600766452_'),
(2, 5, 'SmartHealth Maxi Violet', 'Melindungi langkah hidup untuk meraih masa depan Anda & keluarga', '<p>SmartHealth Maxi Violet merupakan asuransi kesehatan yang dapat digunakan secara perorangan dan keluarga.</p>\r\n', '<ul>\r\n	<li>SmartHealth Maxi Violet menyediakan berbagai fasilitas medis dengan kemudahan Cashless (pembayaran non-tunai) di rumah sakit jaringan perusahaan Allianz-Admedika di Indonesia.</li>\r\n	<li>SmartHealth Maxi Violet juga menyediakan Plan yang bervariasi dan dapat disesuaikan dengan kebutuhan pemegang polis beserta keluarga.</li>\r\n	<li>Pemegang polis mendapatkan Manfaat Rawat Inap disertai Manfaat Pilihan berupa Rawat Jalan, Persalinan, Rawat Gigi dan Manfaat Tambahan Pilihan berupa Santunan Harian untuk tambahan biaya rawat inap Pilihan sistem pembayaran klaim Reimbursement dan eAZy Claim bila perawatan dilakukan di luar Jaringan perusahaan asuransi Allianz.</li>\r\n	<li>No Claim Bonus jika tidak ada catatan klaim selama 1 tahun polis dan pemegang polis melakukan perpanjangan polis, maka polis perpanjangan akan mendapatkan diskon bonus yang dihitung dari premi polis tahun lalu.</li>\r\n	<li>Diskon ini berlaku untuk premi per Tertanggung. Diskon premi 5% apabila pemegang polis juga mengikutsertakan anggota keluarga Dalam SmartHealth Maxi Violet, tidak ada batasan tahunan dan tidak ada masa tunggu, kecuali untuk program Persalinan.</li>\r\n</ul>\r\n', '1600766703_tapro.jpg'),
(2, 6, 'Asuransi Kesehatan Allisya Care', 'Asuransi kesehatan perorangan atau keluarga dengan berbagai pilihan Plan yang mengerti kebutuhan setiap pemegang polis', '<p>AlliSya Care merupakan produk asuransi kesehatan dari Allianz yang dikelola secara syariah di mana peserta saling tolong-menolong dengan peserta lainnya melalui kontribusi yang dibayarkan untuk menghadapi suatu peristiwa yang tidak diharapkan.</p>\r\n\r\n<p>AlliSya Care dapat digunakan sebagai asuransi kesehatan perorangan atau keluarga dengan berbagai pilihan Plan yang mengerti kebutuhan setiap pemegang polis.</p>\r\n\r\n<p>Sebagai asuransi kesehatan, AlliSya Care menyediakan Manfaat Rawat Inap dan memberikan penggantian biaya perawatan bila pemegang polis atau keluarga sakit serta menyediakan berbagai Manfaat Pilihan seperti Persalinan, Rawat Jalan, Rawat Gigi, dan Santunan Harian.</p>\r\n', '<ul>\r\n	<li>Asuransi Allisya Care dikelola secara syariah di mana para peserta saling tolong-menolong dengan peserta lainnya melalui premi yang dibayarkan untuk menghadapi suatu peristiwa yang tidak diharapkan.</li>\r\n	<li>Fasilitas medis dengan kemudahan Cashless (pembayaran non-tunai) di rumah sakit jaringan perusahaan Allianz-Admedika di Indonesia.</li>\r\n	<li>Menyediakan manfaat Rawat Inap disertai Manfaat Pilihan yaitu Rawat Jalan, Rawat Gigi, Persalinan, dan Santunan Harian bagi pemegang polis asuransi Allisya Care.</li>\r\n	<li>Fasilitas eAZy Claim dan Reimbursement untuk rumah sakit di luar jaringan perusahaan Allianz-Admedika.</li>\r\n	<li>Pembagian Surplus Underwriting (diambil dari dana tabarru yang terkumpul dan dibagikan kepada Peserta) yang tersedia bagi pemegang polis bila tidak terjadi klaim dan melakukan perpanjangan polis.</li>\r\n	<li>Potongan kontribusi 5% apabila pemegang polis mengikutsertakan anggota keluarga.</li>\r\n	<li>Tersedia manfaat tambahan seperti evakuasi medis darurat, biaya pemulihan kesehatan, serta bantuan informasi medis 24 jam di seluruh dunia bagi pemegang polis asuransi Allisya Care.</li>\r\n</ul>\r\n', '1600766948_Allisya Care.png'),
(3, 7, 'Asuransi Kendaraan', 'Asuransikan Kendaraan Terbaik Bagi Kendaraan Anda', '<p>Asuransi kendaraan yang komprehensif, tidak ribet dengan berbagai pilihan perlindungan</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Pencurian kendaraan oleh supir pribadi</li>\r\n	<li>Bantuan darurat 24 jam (Allianz Roda)</li>\r\n	<li>Perlindungan banjir hingga water hammer</li>\r\n	<li>Akomodasi taksi</li>\r\n	<li>Jaringan bengkel &amp; AllianzCare 24/7 hari</li>\r\n	<li>Tanggung Jawab Hukum Pihak Ketiga</li>\r\n</ul>\r\n', '1600767326_mobilku.png'),
(4, 8, 'Allianz RumahKu Plus', 'Asuransi Yang Memberikan Perlindungan Maksimal Bagi Hunian Keluarga', 'Asuransi yang memberi jaminan perlindungan untuk hunian yang komprehensif, lebih dari yang dibayangkan', 'Jaminan perlindungan untuk hunian yang komprehensif, lebih dari yang dibayangkan\r\nAkomodasi Sementara\r\nTanggung Gugat Hukum\r\nKerusakan Harta Benda\r\nGanti Rugi atas Kematian Tertanggung\r\nKebakaran\r\nTeroris dan Sabotase', '1600767544_rumahku.png'),
(4, 9, 'Dana Pensiun Lembaga Keuangan (DPLK)', '', '<p>DPLK Allianz Indonesia adalah Program Dana Pensiun Lembaga Keuangan bagi perusahaan dan karyawannya.</p>\r\n\r\n<p>Selain program pensiun, DPLK Allianz Indonesia juga dapat digunakan sebagai program pesangon.</p>\r\n', '<ul>\r\n	<li>Berdiri sejak tahun 1996, program dana pensiun Allianz Indonesia memiliki pengalaman dan kredibilitas dalam mengelola Program Pensiun dari berbagai jenis industri di Indonesia yang sesuai dengan kebutuhan perusahaan dan karyawan.</li>\r\n	<li>Program dana pensiun Allianz Indonesia dikelola tim investasi yang berpengalaman dan Allianz Investment Management (AIM) dengan prinsip filosofi investasi yang hati-hati.</li>\r\n	<li>Pelayanan dan administrasi didukung Key Account Management dari Allianz, serta sistem Teknologi Informasi yang memudahkan akses peserta dalam mengecek dana pensiun.</li>\r\n	<li>Program dana pensiun ini juga menawarkan pilihan investasi yang beragam di mana perusahaan dapat memilih salah satu atau mengombinasikan pilihan investasi yang tersedia.</li>\r\n	<li>Pilihan iuran yang fleksibel para peserta program dana pensiun dan bisa disesuaikan dengan kebutuhan atau kemampuan finansial perusahaan.</li>\r\n	<li>Pilihan pembayaran dana pensiun juga mempunyai manfaat yang beragam seperti Manfaat Pensiun Normal, Manfaat Pensiun Dipercepat, Manfaat Pensiun Meninggal, dan Manfaat Pensiun Cacat.</li>\r\n	<li>Iuran perusahaan akan dianggap sebagai biaya sehingga dapat mengurangi pajak penghasilan badan. Selain itu, penempatan dana peserta di instrumen deposito tidak dikenakan pajak.</li>\r\n</ul>\r\n', '1601027484_'),
(2, 10, 'SmartHealth Classic Premier', '', '<p>Hadir dengan perlindungan yang maksimal bagi kebutuhan perusahaan dalam memberikan kenyamanan dari sisi perawatan kesehatan bagi karyawan beserta keluarganya.</p>\r\n\r\n<p>Produk ini menyediakan perlindungan untuk biaya Rawat Inap di Rumah Sakit serta biaya untuk Melahirkan di Rumah Sakit (manfaat tambahan).</p>\r\n', '<ul>\r\n	<li>Penggantian yang maksimal dengan sistem As charged (kecuali untuk manfaat kelas kamar berdasarkan Plan yang pilih perusahaan)</li>\r\n	<li>Penghapusan masa tunggu untuk penyakit yang sudah ada sebelumnya (Pre Existing Condition) untuk minimum peserta 100 orang (terdiri dari karyawan &amp; keluarganya)</li>\r\n	<li>Perlindungan perawatan kesehatan hingga maksimum 70 tahun</li>\r\n	<li>Premi dihitung berdasarkan usia rata-rata peserta dewasa</li>\r\n	<li>Memberikan kemudahan bagi karyawan dengan istem klaim Cashless di Rumah Sakit rekanan Allianz.</li>\r\n	<li>Tersedia fasilitas Profit Sharing bagi perusahaan dengan ketentuan yang berlaku</li>\r\n</ul>\r\n', '1601027609_'),
(2, 11, 'SmartHealth Blue Sapphire', '', '<p>SmartHealth Blue Sapphire merupakan program Asuransi kesehatan kumpulan yang sangat komprehensif, sehingga dapat membantu perusahaan dalam memberikan perlindungan kesehatan bagi karyawan beserta keluarganya.</p>\r\n\r\n<p>SmartHealth Blue Sapphire memberikan perlindungan untuk Rawat Inap dan beberapa perlindungan tambahan lainnya sebagai pilihan.</p>\r\n', '<ul>\r\n	<li>Persyaratan yang mudah untuk memiliki SmartHealth Blue Sapphire, hanya dengan minimum peserta 10 orang (terdiri dari karyawan &amp; keluarganya) serta minimum Premi Rp.2,500,000.</li>\r\n	<li>Tersedia fasilitas sistem Klaim dengan Cashless atau Reimburse sebagai pilihan</li>\r\n	<li>Perlindungan yang lengkap mulai dari Rawat Inap, Rawat Jalan, Melahirkan, Perawatan gigi hingga untuk Kaca Mata</li>\r\n	<li>Perlindungan hingga peserta berusia 70 tahun</li>\r\n	<li>Terdapat pilihan untuk co share mulai dari 20% hingga 0%</li>\r\n	<li>Penghapusan masa tunggu untuk penyakit yang sudah ada sebelumnya( Pre Existing Condition) untuk minimum peserta 100 orang (terdiri dari karyawan &amp; keluarganya)</li>\r\n	<li>Tersedia fasilitas Profit Sharing bagi perusahaan dengan ketentuan yang berlaku</li>\r\n</ul>\r\n', '1601027659_'),
(2, 12, 'SmartHealth Light Titanium', '', '<p>SmartHealth Light Titanium merupakan program Asuransi kesehatan kumpulan yang sangat komprehensif dengan pembayaran melalui mata uang USD.</p>\r\n\r\n<p>SmartHealth Light Titanium dirancang untuk membantu perusahaan dalam memberikan perlindungan kesehatan bagi karyawan beserta keluarganya.</p>\r\n\r\n<p>SmartHealth Light Titanium memberikan perlindungan untuk Rawat Inap dan beberapa perlindungan tambahan lainnya sebagai pilihan.</p>\r\n', '<ul>\r\n	<li>Persyaratan yang mudah untuk memiliki SmartHealth Blue Sapphire, hanya dengan minimum peserta 10 orang (terdiri dari karyawan &amp; keluarganya) serta minimum Premi Rp.2,500,000.</li>\r\n	<li>Tersedia fasilitas sistem Klaim dengan Cashless atau Reimburse sebagai pilihan</li>\r\n	<li>Perlindungan yang lengkap mulai dari Rawat Inap, Rawat Jalan, Melahirkan, Perawatan gigi hingga untuk Kaca Mata</li>\r\n	<li>Perlindungan hingga peserta berusia 70 tahun</li>\r\n	<li>Terdapat pilihan untuk co share mulai dari 20% hingga 0%</li>\r\n	<li>Penghapusan masa tunggu untuk penyakit yang sudah ada sebelumnya( Pre Existing Condition) untuk minimum peserta 100 orang (terdiri dari karyawan &amp; keluarganya)</li>\r\n	<li>Tersedia fasilitas Profit Sharing bagi perusahaan dengan ketentuan yang berlaku.</li>\r\n</ul>\r\n', '1601027725_'),
(5, 13, 'Allianz Tasbih', 'Membantu Anda dalam mewujudkan impian untuk menunaikan ibadah haji', '<p>Untuk mewujudkan niat ibadah haji sebagai salah satu dari lima pondasi (rukun) memerlukan kesiapan batin serta tersedianya dana haji untuk Biaya Penyelenggaraan Ibadah Haji (BPIH).</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Dengan perencanaan tahapan dana haji yang tepat dari Allianz Syariah, niat haji Anda dapat direalisasikan dan diwujudkan dengan tambahan manfaat perlindungan bagi Anda dan keluarga tercinta.</p>\r\n', '<ul>\r\n	<li>Santunan asuransi jiwa apabila peserta asuransi mengalami kecelakaan atau meninggal</li>\r\n	<li>Evakuasi medis peserta asuransi dan repatriasi darurat&nbsp; &nbsp;</li>\r\n	<li>Manfaat santunan asuransi jiwa 200% apabila meninggal dunia saat perjalanan haji atau meninggal dunia di Mekkah dan Medinah saat ibadah umroh</li>\r\n</ul>\r\n', '1601027865_'),
(5, 14, 'Fitur Wakaf', 'Produk asuransi Jiwa Unit Link AlliSya Protection Plus agar Anda semakin terlindungi dan terberkahi', '<h1>Salah satu amalan dalam Islam melalui penyaluran harta benda untuk kepentingan masyarakat serta mendapatkan keberkahan pahala yang tidak terputus sampai akhirat kelak.</h1>\r\n', '<ul>\r\n	<li>Bernilai guna saat orang yang mewakafkan (wakif) meninggal dunia.</li>\r\n	<li>Bermanfaat untuk jangka panjang.</li>\r\n	<li>Bersifat sukarela dan produktif untuk kepentingan masyarakat.</li>\r\n	<li>Nilai ekonomi yang tidak berkurang dan dikembangkan secara prinsip syariah.</li>\r\n</ul>\r\n', '1601027983_');

